mapper.insert:

INSERT INTO admin.NOTI 
( 
	  id
	, title
	, description
	, createDate
	, createUser
	, updateDate
	, updateUser
)
VALUES 
(	
	  id_seq.nextval 
	, #{title}
	, #{description}
	, SYSDATE
	, #{createUser}
	, SYSDATE
	, #{createUser}
)

mapper.update:

UPDATE admin.NOTI
SET 
	title        = #{title}
  , description  = #{description}
  , updateDate   = SYSDATE
  , updateUser   = #{createUser}
WHERE id = #{id}
