mapper.insert:

INSERT INTO admin.contents 
( 
	   contentsId
     , fileName
     , fileUrl
     , createDate
     , createUser
     , updateDate
     , updateUser
     , description
     , contentsName
)
VALUES 
(	
	  contentsId_seq.nextval 
	, #{fileName}
	, #{fileUrl}
	, SYSDATE
	, #{createUser}
	, SYSDATE
	, #{createUser}
	, #{description}
	, #{contentsName}
)

mapper.update:

UPDATE admin.contents
SET 
	description   = #{description}
  , contentsName  = #{contentsName}
  , fileName  	  = #{fileName}
  , fileUrl  	  = #{fileUrl}
  , updateDate    = SYSDATE
  , updateUser    = #{createUser}
WHERE contentsId = #{contentsId}
